#suppression du @ des élément de la liste initiale
journalists_list = journalists_list_init.map do |journalist|
  journalist.gsub("@", "")
end


#Nombre d'éléments de la liste
journalists_number = journalists_list.count
puts "Combien y a-t-il d'éléments dans cette liste ?"
puts journalists_number
puts " "

#Elément le plus court
shorter_element = journalists_list.min_by { |element| element.length }
puts "Quel est le nom le + court de cette liste ? "
puts "L'élément le plus court est : #{shorter_element}"
puts "Cet élément comporte #{shorter_element.length} caractères."
puts " "

#Elements à 5 caractères
elements_count_5 = journalists_list.count {|element|element.length == 5}
puts "Combien y-a-t'il d'éléments contenant 5 caractères"
puts "Il y a #{elements_count_5} éléments de 5 caractères"
puts " "

#Combien d'éléments commencent par une majuscule ? 
count_majuscules = 0 # Initialise la variable à zéro
journalists_list.each do |journalist| #on va parcourir la liste de journalistes ligne par ligne
  if journalist[0] == journalist[0].upcase #on regarde le 1er caractère de chaque élément de la liste (valeur 1) // On convertit le 1er caractère en majuscule (Valeur 2) 
    #on compare le 1er caratère de val1 et val 2. Si c'est identique, voir résultat ci dessus
    count_majuscules +=1 #on augmente de +1 la variable count_majuscules à chaque fois que la condition ci dessus est remplie
  end
end
puts "Combien d'éléments commencent par une majuscule ?"
puts "Il y a #{count_majuscules} éléments qui commencent par une majuscule"
puts ""

#Trier la liste par ordre alphabetique
